settings.custom_liquid:
{% assign section1bulletsarray = product.metafields.copy.section-1-bullets | split: ';' %}
{% assign section2bulletsarray = product.metafields.copy.section-2-bullets | split: ';' %}
{% if product.metafields.copy.headline-1 != blank %}
<div class="container customdescriptioncontainer">
    <div class="one-half columns medium-down--one-whole customdescriptioncolumn flexorder-2">
        <h2>{{ product.metafields.copy.headline-1 }}</h2>
        <p class="product-subheadline">{{ product.metafields.copy.subheadline-1 }}</p>
        <p>{{ product.metafields.copy.text-1 | newline_to_br }}</p>
        <ul>{% for bullet in section1bulletsarray %}
                <li>{{ bullet }}</li>
            {% endfor %}
        </ul>
    </div>
    <div class="one-half columns medium-down--one-whole customdescriptioncolumn flexorder-1">
        {{ product.metafields.copy.video-1 }}
    </div>
</div>
<div class="product_page_divider"></div>
{% endif %}
{% if product.metafields.copy.headline-2 != blank %}
<div class="container customdescriptioncontainer">
    <div class="one-half columns medium-down--one-whole customdescriptioncolumn flexorder-1">{{ product.metafields.copy.video-2 }}</div>
    <div class="one-half columns medium-down--one-whole customdescriptioncolumn flexorder-2">
        <h2>{{ product.metafields.copy.headline-2 }}</h2>
        <p class="product-subheadline">{{ product.metafields.copy.subheadline-2 }}</p>
        <p>{{ product.metafields.copy.text-2 | newline_to_br }}</p>
        <ul>{% for bullet in section2bulletsarray %}
                <li>{{ bullet }}</li>
            {% endfor %}
        </ul>
    </div>
</div>
{% endif %}
